import logo from './logo.svg';
import './App.css';
import React from 'react';

function App() {
  return (
    <div className="App">
      <FullName FirstName = 'Daniel' LastName = 'Mykytiuk'/>
    </div>
  );
}



function FullName(props) {
  console.log(props);
  let {FirstName:f, LastName:l}=props;
  return(
    <div>
      <h1>My Full Name</h1>
      <p>My name is <strong>{f} {l}</strong></p>
    </div>

  );
}

class PhoneNumber extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      phoneNumber: '',
      phoneNumberError: ''
    }
  }
  handleChange = (e) => {
    const {name, value} = e.target;
    this.setState({
      [name]: value
    })
  }
  handleSubmit = (e) => {
    e.preventDefault();
    const {phoneNumber} = this.state;
    const phoneNumberError = this.validatePhoneNumber(phoneNumber);
    this.setState({
      phoneNumberError
    })
  }
  validatePhoneNumber = (phoneNumber) => {
    const phoneNumberError = '';
    if(phoneNumber.length < 10){
      return 'Phone number must be 10 digits';
    }
    return phoneNumberError;
  }
  render(){
    const {phoneNumber, phoneNumberError} = this.state;
    return(
      <div>
        <h1>Phone Number</h1>
        <form onSubmit={this.handleSubmit}>
          <input type="text" name="phoneNumber" value={phoneNumber} onChange={this.handleChange}/>
          <button type="submit">Submit</button>
        </form>
        {phoneNumberError && <p>{phoneNumberError}</p>}
      </div>
    );
  }
}

export default App;
